import { useState, useCallback } from 'react';
import { Layout, Card, FormLayout, TextField, Button } from '@shopify/polaris';

const SubmitForm = () => {
    const [inputs, setInputs] = useState({ name: '', title: '' });

    // const handleChange = useCallback((value, id) =>
    //     setInputs({
    //         ...inputs,
    //         [id]: value
    //     })
    //     ,
    //     []);

    const handleChange = (value, id) => {
        let idName = id;
        setInputs({
            ...inputs,
            idName: value
        })
        console.log(inputs);
    }

    const handleSubmit = () => {
        console.log(inputs)
    }

    return (
        <Layout>
            <Layout.AnnotatedSection
                title='Submit your problem'
                description='Specify what you are trouble in'
            >
                <Card sectioned>
                    <FormLayout>
                        <TextField label='Name' id='name' name='name' value={inputs.name} onChange={handleChange} />
                        <TextField label='Title' id='title' name='title' value={inputs.title} onChange={handleChange} />
                        <Button submit onClick={handleSubmit}>Submit</Button>
                    </FormLayout>
                </Card>
            </Layout.AnnotatedSection>
        </Layout>
    )
}

export default SubmitForm;
