import { useState, useCallback } from 'react';
import { Layout, Card, FormLayout, TextField, Button } from '@shopify/polaris';

const SubmitForm = () => {
    const [inputs, setInputs] = useState({ name: '1', title: '' });

    const handleChange = useCallback((value) =>
        // setInputs({
        //     ...inputs,
        //     name: value
        // }),
        setInputs(preState =>
            preState = value
        ),
        []);

    const handleSubmit = () => {
        console.log(inputs)
    }

    return (
        <Layout>
            <Layout.AnnotatedSection
                title='Submit your problem'
                description='Specify what you are trouble in'
            >
                <Card sectioned>
                    <FormLayout>
                        <TextField label='Name' value={inputs.name} onChange={handleChange} />
                        <TextField label='Title' value={inputs.title} onChange={handleChange} />
                        <Button submit onClick={handleSubmit}>Submit</Button>
                    </FormLayout>
                </Card>
            </Layout.AnnotatedSection>
        </Layout>
    )
}

export default SubmitForm;
