import { useState } from 'react';
import { Layout, Card, FormLayout, TextField } from '@shopify/polaris';

const SubmitForm = () => {
    const [inputs, setInputs] = useState();

    const handleChange = (val) => {
        setInputs({ val });
        console.log(inputs)
    }

    return (
        <Layout>
            <Layout.AnnotatedSection
                title='Submit your problem'
                description='Specify what you are trouble in'
            >
                <Card sectioned>
                    <FormLayout>
                        <TextField label='Name' onChange={(val) => handleChange(val)} />
                    </FormLayout>
                </Card>
            </Layout.AnnotatedSection>
        </Layout>
    )
}

export default SubmitForm;
