    }

    return (
        <Layout>
            <Layout.AnnotatedSection
                title='Submit your problem'
                description='Specify what you are trouble in'
            >
                <Card sectioned>
                    <FormLayout>
                        <TextField label='Name' name='name' onChange={handleChange} />
                        <TextField label='Title' name='title' onChange={handleChange} />
                    </FormLayout>
                </Card>
            </Layout.AnnotatedSection>
        </Layout>
    )
}

export default SubmitForm;
